<?php$roomId = $_POST['roomId'];$mode = $_POST['mode'];$mysql = new SaeMysql();if($mode == "isNeedAnswer") {		$queryQuestion = "select question from chatroom where id=" . $roomId;	$questionArr = $mysql->getData($queryQuestion);	$question = "";	if(is_array($questionArr)) {		$question = $questionArr[0]['question'];	}	echo $question;	} else if($mode == "checkAnswer") {	$answerReq = $_POST['answer'];	trim($answerReq);	if(!empty($answerReq)) {		$queryAnswer = "select answer from chatroom where id=" . $roomId;		$answerArr = $mysql->getData($queryAnswer);				$answer = "";		if(is_array($answerArr)) {			$answer = $answerArr[0]['answer'];		}		if($answer == md5($answerReq)) {			echo true;			return;		}	}		echo false;}$mysql->closeDb();?>